import React, { Component } from 'react';

class Coin extends Component {
  constructor(props) {
    super(props);
  }

  render() {
    const { flip, count, heads, tails } = this.props.vals;

    const myHeads = this.props.headCoin;
    const myTails = this.props.tailCoin;

    return (
      <div>
        {flip === 1 ? myHeads : myTails}
        <p>
          Out of {count} flips, there have been {heads} heads and {tails} tails{' '}
        </p>
      </div>
    );
  }
}

export default Coin;
