import React, { Component } from 'react';

class FlipCoin extends Component {
  constructor() {
    super();
    this.state = {
      flip: 0,
      count: 0,
      heads: 0,
      tails: 0
    };
    this.handleClick = this.handleClick.bind(this);
  }

  flipFunc() {
    return { flip: Math.floor(Math.random() * 2) + 1 };
  }

  coinCount(curCount) {
    // console.log({ count: curCount.count + 1 });
    return { count: curCount.count + 1 };
  }

  headsTails(headCnt, tailCnt) {
    if (this.state.flip === 1) {
      return { heads: headCnt.heads + 1 };
    } else if (this.state.flip === 2) {
      return { tails: tailCnt.tails + 1 };
    }
  }

  handleClick() {
    this.setState(this.flipFunc);
    this.setState(this.coinCount);
    this.setState(this.headsTails);
    // console.log(this.state.count);
    console.log(this.state.flip);
    console.log(this.state.heads);
  }

  render() {
    return (
      <div>
        <h1>Flip Coin Component</h1>
        <button onClick={this.handleClick}>Flip Coin</button>
      </div>
    );
  }
}

export default FlipCoin;
