import React, { Component } from 'react';

class Coin extends Component {
  constructor(props) {
    super(props);
  }
  render() {
    const { count, heads, tails } = this.props.vals;

    return (
      <div>
        <p>
          Out of {count} flips, there have been {heads} heads and {tails} tails{' '}
        </p>
      </div>
    );
  }
}

export default Coin;

