import React, { Component } from 'react';
import Coin from './Coin';

class FlipCoin extends Component {
  constructor() {
    super();
    this.state = {
      flip: 0,
      count: 0,
      heads: 0,
      tails: 0
    };
    this.handleClick = this.handleClick.bind(this);
  }

  // flipFunc() {
  //   return { flip: Math.floor(Math.random() * 2) + 1 };
  // }

  coinCount(curCount) {
    // console.log({ count: curCount.count + 1 });

    return { count: curCount.count + 1 };
  }

  headsTails(curCount) {
    let randN = Math.floor(Math.random() * 2) + 1;

    if (this.state.flip === 1) {
      return { heads: curCount.heads + 1 };
    }
    if (this.state.flip === 2) {
      return { tails: curCount.tails + 1 };
    }
    return { flip: randN };
  }

  handleClick() {
    // this.setState(this.flipFunc);
    this.setState(this.coinCount);
    this.setState(this.headsTails);
  }

  pickPic() {
    let pic;

    if (this.state.flip === 1) {
      pic = <img alt="" src={this.props.headCoin} />;
    }
    if (this.state.flip === 2) {
      pic = <img alt="" src={this.props.tailCoin} />;
    }

    return pic;
  }

  render() {
    let rick = this.pickPic();

    return (
      <div>
        <h1>Let's Flip a coin</h1>
        {rick}
        <button onClick={this.handleClick}>Flip Coin</button>
        <Coin
          vals={this.state}
          // headCoin="https://random-ize.com/coin-flip/us-quarter/us-quarter-front.jpg"
          // tailCoin="https://random-ize.com/coin-flip/us-quarter/us-quarter-back.jpg"
        />
      </div>
    );
  }
}

export default FlipCoin;
