import React, { Component } from 'react';

class Coin extends Component {
  render() {
    const { flip, count, heads, tails } = this.props.vals;

    let pic;

    if (flip === 1) {
      pic = <img alt="" src={this.props.headCoin} />;
    }
    if (flip === 2) {
      pic = <img alt="" src={this.props.tailCoin} />;
    } else {
      return '';
    }

    return (
      <div>
        {pic}
        <p>
          Out of {count} flips, there have been {heads} heads and {tails} tails{' '}
        </p>
      </div>
    );
  }
}

export default Coin;
